import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [showMenu, setShowMenu] = useState(false); // 状态用于控制菜单显示与否
  const [boxDisplay, setBoxDisplay] = useState('none'); // 初始状态为 'none'

  // 窗口大小改变时，调整 box 元素的显示方式
  useEffect(() => {
    function handleResize() {
      if (boxDisplay !== 'none') {
        if (window.innerWidth < 500) {
          setBoxDisplay('flex');
        } else {
          setBoxDisplay('grid');
        }
      }
    }

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [boxDisplay]);

  // 点击 Call to Action 按钮，显示 box 元素
  const handleCTAClick = () => {
    setBoxDisplay('block');
    adjustBoxDisplay();
  };

  // 点击菜单按钮，切换菜单显示状态
  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  // 返回 JSX 元素
  return (
    <div className="App">
      <nav className="navbar">
        <div className="nav-left">
          <p>Website Title / Logo</p>
        </div>
        <ul className="menu">
          <li><a href="#">item1</a></li>
          <li><a href="#">item2</a></li>
          <li><a href="#">item3</a></li>
          <li><a href="#">item4</a></li>
        </ul>
        <div className="menu-btn" onClick={toggleMenu} style={{ display: showMenu ? 'none' : 'block' }}>&#9776;</div>
      </nav>
      <nav className={`mobile-menu ${showMenu ? 'show' : ''}`}>
        <div className="close-btn" onClick={toggleMenu} style={{ display: showMenu ? 'block' : 'none' }}>&times;</div>
        <ul className="mobile-list">
          <li><a href="#">item1</a></li>
          <li><a href="#">item2</a></li>
          <li><a href="#">item3</a></li>
          <li><a href="#">item4</a></li>
        </ul>
      </nav>
      <header className="header">
        Welcome Message
      </header>
      <main className="main-content">
        <section className="section">Section Title</section>
        <div className="grid-container">
          <div className="box">Content Box 1</div>
          <div className="box">Content Box 2</div>
          <div className="box">Content Box 3</div>
          <div className="box">Content Box 4</div>
        </div>
        <div className="btn" onClick={handleCTAClick}>Call to Action</div>
        <div className="hidden-box" style={{ display: boxDisplay }}>
          <div className="box">Content Box 5</div>
          <div className="box">Content Box 6</div>
          <div className="box">Content Box 7</div>
          <div className="box">Content Box 8</div>
        </div>
      </main>
    </div>
  );
}

export default App;
