import React, { useState } from 'react';
import './App.css';

function App() {
  return (
    <div className="App">
      <Navbar />
      <Header />
      <MobileMenu />
      <MainContent />
    </div>
  );
}

function Navbar() {
  return (
    <nav className="navbar">
      <div className="nav-left">
        <p>Website Title / Logo</p>
      </div>
      <ul className="menu">
        <li><a href="#">item1</a></li>
        <li><a href="#">item2</a></li>
        <li><a href="#">item3</a></li>
        <li><a href="#">item4</a></li>
      </ul>
      <MobileMenuToggle />
    </nav>
  );
}

function MobileMenuToggle() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <div className="menu-btn" onClick={toggleMobileMenu} style={{ display: isMobileMenuOpen ? 'none' : 'block' }}>
      &#9776;
    </div>
  );
}

function MobileMenu() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className={`mobile-menu ${isMobileMenuOpen ? 'show' : ''}`}>
      <div className="close-btn" onClick={toggleMobileMenu}>&times;</div>
      <ul className="mobile-list">
        <li><a href="#">item1</a></li>
        <li><a href="#">item2</a></li>
        <li><a href="#">item3</a></li>
        <li><a href="#">item4</a></li>
      </ul>
    </nav>
  );
}

function Header() {
  const [welcomeMessage, setWelcomeMessage] = useState('Welcome Message');

  const handleClick = () => {
    setWelcomeMessage('Have a Good Time!');
  };

  return (
    <header className="header" onClick={handleClick}>
      {welcomeMessage}
    </header>
  );
}

function MainContent() {
  const [isHiddenBoxVisible, setIsHiddenBoxVisible] = useState(false);

  const handleCTAClick = () => {
    setIsHiddenBoxVisible(!isHiddenBoxVisible);
  };

  return (
    <main className="main-content">
      <section className="section">Section Title</section>
      <div className="grid-container">
        <div className="box">Content Box 1</div>
        <div className="box">Content Box 2</div>
        <div className="box">Content Box 3</div>
        <div className="box">Content Box 4</div>
      </div>
      <div className="btn" onClick={handleCTAClick}>Call to Action</div>
      {isHiddenBoxVisible && (
        <div className="hidden-box">
          <div className="box">Content Box 5</div>
          <div className="box">Content Box 6</div>
          <div className="box">Content Box 7</div>
          <div className="box">Content Box 8</div>
        </div>
      )}
    </main>
  );
}

export default App;
