import React, { useState } from 'react';
import './App.css'; // 引入你的 CSS 文件

function App() {
  const [showMenu, setShowMenu] = useState(false); // 狀態用於控制菜單顯示與否

  // 點擊菜單按鈕時觸發的函數
  const toggleMenu = () => {
    setShowMenu(!showMenu); // 切換菜單顯示狀態
  };

    const [boxDisplay, setBoxDisplay] = useState('none'); // 狀態用於控制 box 元素的顯示方式

    // 窗口大小改變時，調整 box 元素的顯示方式
    useEffect(() => {
      const adjustBoxDisplay = () => {
        if (window.innerWidth < 500) {
          setBoxDisplay('flex');
        } else {
          setBoxDisplay('grid');
        }
      };

      adjustBoxDisplay(); // 頁面首次加載時執行一次
      window.addEventListener('resize', adjustBoxDisplay); // 添加窗口大小變化的監聽器

      return () => {
        window.removeEventListener('resize', adjustBoxDisplay); // 移除監聽器，避免內存洩漏
      };
    }, []);

    // 點擊 Call to Action 按鈕，顯示 box 元素
    const handleCTAClick = () => {
      setBoxDisplay('block');
    };
  // 返回 JSX 元素
  return (
    <div className="App">
      <nav className="navbar">
        <div className="nav-left">
          <p>Website Title / Logo</p>
        </div>
        <ul className="menu">
          <li><a href="#">item1</a></li>
          <li><a href="#">item2</a></li>
          <li><a href="#">item3</a></li>
          <li><a href="#">item4</a></li>
        </ul>
        <div className="menu-btn" onClick={toggleMenu} style={{ display: showMenu ? 'none' : 'block' }}>&#9776;</div>
      </nav>
      <nav className={`mobile-menu ${showMenu ? 'show' : ''}`}>
        <div className="close-btn" onClick={toggleMenu} style={{ display: showMenu ? 'block' : 'none' }}>&times;</div>
        <ul className="mobile-list">
          <li><a href="#">item1</a></li>
          <li><a href="#">item2</a></li>
          <li><a href="#">item3</a></li>
          <li><a href="#">item4</a></li>
        </ul>
      </nav>
      <header className="header">
        Welcome Message
      </header>
      <main className="main-content">
        <section className="section">Section Title</section>
        <div className="grid-container">
          <div className="box">Content Box 1</div>
          <div className="box">Content Box 2</div>
          <div className="box">Content Box 3</div>
          <div className="box">Content Box 4</div>
        </div>
        <div className="btn" onClick={() => console.log('Call to Action clicked')}>Call to Action</div>
        <div className="hidden-box">
          <div className="box">Content Box 5</div>
          <div className="box">Content Box 6</div>
          <div className="box">Content Box 7</div>
          <div className="box">Content Box 8</div>
        </div>
      </main>
    </div>
  );
}

export default App;
